/* css reset */
* {
    box-sizing: border-box;
}

*:before,
*::after {
    box-sizing: border-box;
}

* {
    margin: 0;
    padding: 0;
}

ul,
ol {
    list-style: none;
}

a {
    text-decoration: none;
    color: #000;
}

/* style */
body {
    background: #eee;
    background: url("https://api.dujin.org/bing/1920.php");
    /**
    * 必应接口来自缙哥哥博客
    * https://www.dujin.org/3618.html
    */
}

.globalHeader {
    margin: 20px;

}

@media (min-width: 500px) {
    .globalHeader {
        margin: 160px 0 100px;
    }
}

.searchForm {
    display: flex;
    justify-content: space-between;

}

@media (min-width: 500px) {
    .searchForm {
        max-width: 400px;
        margin-left: auto;
        margin-right: auto;
    }
}


.searchForm>input {
    width: 100%;
    margin-right: 10px;
    height: 40px;
    padding: 0 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
}

.searchForm>button {
    white-space: nowrap;
    padding: 0 28px;
    border: none;
    border-radius: 4px;
    background: #0282b9;
    color: white;
    font-size: 16xp;
}



.globalMain {
    max-width: 900px;
    margin-left: auto;
    margin-right: auto;
}


.siteList {
    margin: 30px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
}

@media (min-width: 500px) {
    .siteList {
        margin-left: 0;
        margin-right: -37px;
        justify-content: flex-start;
    }
}

.siteList>li {
    margin-bottom: 10px;
}

@media(min-width:500px) {
    .siteList>li {
        margin-right: 25px;

    }
}

.siteList .site {
    width: 160px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    background: white;
    margin-bottom: 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
    padding: 20px 0;
    position: relative;
}

.siteList .site>.logo {
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 64px;
}

.siteList .site>.link {
    font-size: 14px;
    margin-top: 4px;
}

.siteList .site>.close {
    position: absolute;
    right: 5px;
    top: 3px;
    display: none;
}

.siteList .site:hover>.close {
    display: block;
}

.siteList .addButton {
    width: 160px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    padding: 20px 0;
    height: 129px;
    background: white;
    margin-bottom: 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
}

.siteList .addButton .icon {
    width: 56px;
    height: 56px;
}

.siteList .addButton .text {
    font-size: 14px;
    margin-top: 4px;
}

.siteList .addButton .icon-wrapper {
    width: 64px;
    height: 64px;
    display: flex;
    justify-content: center;
    align-items: center;
}

.dialog {
    background: #fff;
    width: 250px;
    z-index: 999;
    margin: auto;
    border-radius: 4px;

}

.dialog>input {
    width: 100%;
    margin-right: 10px;
    height: 40px;
    padding: 0 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
}

.dialog>button {
    width: 40%;
    height: 30px;
    padding: 0 28px;
    margin: 10px;
    border: none;
    border-radius: 4px;
    background: #0282b9;
    color: white;
    font-size: 10px;
}

.dialogHide {
    display: none;
}
